const delay = ms => new Promise(resolve => setTimeout(resolve, ms))

let FUCKWHYISITNOTWORKING: string[] = [];

async function clickPlay(page: Page) {
  let hasDoneInitialClick = false;
  while (true) {
    page.bringToFront();
    
    await delay(200);
    const elem = await page.$(".play");
    // elem.
    if (elem) {
      // elem.click();
      await page.evaluate((element) => {
        element.click()
      }, elem)
      page.bringToFront();
      await delay(200);
      hasDoneInitialClick = true;
    } else if (hasDoneInitialClick) {
      return;
    } else {
      console.log("Couldn't find play button on page:");
      FUCKWHYISITNOTWORKING.push(await page.content());
      await delay(1000);
    }
  }
}


